type User = {
    /**
     * User id as defined by the database
     */
    user_id: number,
    /**
     * Users first name
     */
    firstName: string
    /**
     * Users last name
     */
    lastName: string
    /**
     * Users email
     */
    email: string
    /**
     * Users password
     */
    password: string
    /**
     * Users token
     */
    userToken: string
}